/**
 * Customizer Controls JS
 * Adds Javascript for Customizer Controls.
 * @package Prologe
 */

! function (t, e) {
    t.customize("prologe_lite_theme_options[blog_content]", function (e) {
        t.customize.control("prologe_lite_theme_options[excerpt_length]", function (t) {
            var o, n;
            n = function () {
                return "excerpt" === e.get()
            }, (o = function () {
                t.active.set(n())
            })(), e.bind(o), t.active.validate = n
        })
    })
}(this.wp, jQuery);
